<script>

export default {
		data() {
			return {
				Request:this.$api.api.prototype,//请求头
			}
		},
		onLaunch: function() {
			this.checkSessionKey();
			
			var that = this;
			wx.getSystemInfo({
				success: function(res) {
					if(res.model.toString().indexOf('iPhone X') != -1) {
						//直接修改VueX的值
						//that.$store.store.state.isIphonex = true;

						//也可以换一种方法，这样可以跟踪每一个状态的变化
						that.$store.store.commit('changePhone', true); 

					}
				}
			});

		}, 
		onReady () {
			wx.getSystemInfo({
				success (res) {
					console.log(res.model)
					console.log(res.pixelRatio)
					console.log(res.windowWidth)
					console.log(res.windowHeight)
					console.log(res.language)
					console.log(res.version)
					console.log(res.platform)
				}
			})
		},
		methods: {
			//检查sessionkey是否过期
			checkSessionKey(){
				var that = this;
				wx.checkSession({
					fail:function(){
						that.login();
					}
				});
			},
			//登陆接口
			async login(){
				await this.$util.util.prototype.getSessionKey()
					.then(res =>{
						console.log(res)
					})
			},
		},
		
	}
</script>

