<template>
	<div class="scroll_top" @click="goTop" v-if="show">
		<div class="icon"><span class="topIcon"></span></div>
	</div>
</template>

<script>
	export default {
		data() {
			return {
				show: false
			}
		},
		props:[],
		methods: {
			//回到顶部
			goTop() {
				wx.pageScrollTo({
					scrollTop: 0,
					duration: 2000
				})
			},
		},
		onPageScroll(e){
			var that  = this;
			if(e.scrollTop >= 120){
				that.show = true;
			}else {
				that.show = false;
			}
		}
	}
</script>

<style scoped>
	
    .scroll_top .icon{
		width: 70rpx;
		height: 70rpx;
		line-height: 70rpx;
		display: inline-block;
		vertical-align: middle;
		
	}
	.scroll_top  .icon span{
		display: inline-block;
		vertical-align: middle;
		width: 70rpx;
		height: 70rpx;
	}
	.scroll_top {
		color: white;
		font-size: 34rpx;
		width:70rpx;
		height:70rpx;
		background-color: transparent;
		position:fixed;
		bottom:130rpx;
		right:40rpx;
	}
    .topIcon {
        background: url('data:image/png;base64,');
        background-size: 100% 100%;
    }

</style>